import React, { Component } from 'react'

import GameCanvas from './components/GameCanvas'
import GameControls from './components/GameControls'

class GameInterface extends Component {
  constructor() {
    super()
    this.getStats = this.getStats.bind(this)
    this.state = {
      paddle1Width: 15,
      paddle1Height: 40,
      paddle1R: 255,
      paddle1G: 255,
      paddle1B: 255,
      paddle1VelocityY: 1
    }
  }

  async getStats() {
    const resp = await fetch(
      'https://wwwforms.suralink.com/pong.php?accessToken=pingPONG'
    ).then(data => {
      return data.json()
    })
    if (resp.gameData.paddle1.width !== 0) {
      this.setState({paddle1Width: resp.gameData.paddle1.width})
      console.log(this.state.paddle1Width)
    }
    console.log(resp.gameData.newDelay)
    setTimeout(this.getStats, this.state.paddle1Width)
  }

  render() {
    this.getStats()

    return (
      <main
        style={{
          width: '100vw',
          height: '100vh',
          background: '#000',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center'
        }}
      >
        <section
          style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center'
          }}
        >
          <GameCanvas {...this.state} />
          <GameControls />
        </section>
      </main>
    )
  }
}

export default GameInterface
